eturn x; &#125; nn::Conv2d conv1, conv2, conv3, conv4; nn::BatchNorm batch_norm1, batch_norm2, batch_norm3;&#125;;TORCH_MODULE(Generatorr);Generatorr generatorr;struct DiscriminatorImpl : nn::Module &#123; DiscriminatorImpl() : conv1(nn::Conv2dOptions(1, 64, 4).stride(2).padding(1).with_bias(false)), leaky_relu1(nn::Functional(torch::leaky_relu, 0.2)), conv2(nn::Conv2dOptions(64, 128, 4).stride(2).padding(1).with_bias(false)), batch_norm2(128), leaky_relu2(nn::Functional(torch::leaky_relu, 0.2)), conv3(nn::Conv2dOptions(128, 256, 4).stride(2).padding(1).with_bias(false)), batch_norm3(256), leaky_relu3(nn::Functional(torch::leaky_relu, 0.2)), conv4(nn::Conv2dOptions(256, 1, 3).stride(1).padding(0).with_bias(false)) &#123; register_module(&quot;conv1&quot;, conv1); register_module(&quot;conv2&quot;, conv2); register_module(&quot;conv3&quot;, conv3); register_module(&quot;conv4&quot;, conv4); register_module(&quot;leaky_relu1&quot;, leaky_relu1); register_module(&quot;leaky_relu2&quot;, leaky_relu2); register_module(&quot;leaky_relu3&quot;, leaky_relu3); register_module(&quot;batch_norm2&quot;, batch_norm2); register_module(&quot;batch_norm3&quot;, batch_norm3); &#125; torch::Tensor forward(torch::Tensor x) &#123; x = leaky_relu1(conv1(x)); x = leaky_relu2(batch_norm2(conv2(x))); x = leaky_relu3(batch_norm3(conv3(x))); x = torch::sigmoid(conv4(x)); return x; &#125; nn::Conv2d conv1, conv2, conv3, conv4; nn::Functional leaky_relu1, leaky_relu2, leaky_relu3; nn::BatchNorm batch_norm2, batch_norm3;&#125;;TORCH_MODULE(Discriminator);Discriminator discriminator;int main(int argc, const char* argv[]) &#123; torch::manual_seed(1); // Create the device we pass around based on whether CUDA is available. torch::Device device(torch::kCPU); if (torch::cuda::is_available()) &#123; std::cout &lt;&lt; &quot;CUDA is available! Training on GPU.&quot; &lt;&lt; std::endl; device = torch::Device(torch::kCUDA); &#125; generatorr-&gt;to(device); discriminator-&gt;to(device); // Assume the MNIST dataset is available under `kDataFolder`; auto dataset = torch::data::datasets::MNIST(kDataFolder) .map(torch::data::transforms::Normalize&lt;&gt;(0.5, 0.5)) .map(torch::data::transforms::Stack&lt;&gt;()); const int64_t batches_per_epoch = std::ceil(dataset.size().value() / static_cast&lt;double&gt;(kBatchSize)); auto data_loader = torch::data::make_data_loader( std::move(dataset), torch::data::DataLoaderOptions().batch_size(kBatchSize).workers(2)); torch::optim::Adam generator_optimizer( generatorr-&gt;parameters(), torch::optim::AdamOptions(2e-4).beta1(0.5)); torch::optim::Adam discriminator_optimizer( discriminator-&gt;parameters(), torch::optim::AdamOptions(2e-4).beta1(0.5)); if (kRestoreFromCheckpoint) &#123; torch::load(generatorr, &quot;generator-checkpoint.pt&quot;); torch::load(generator_optimizer, &quot;generator-optimizer-checkpoint.pt&quot;); torch::load(discriminator, &quot;discriminator-checkpoint.pt&quot;); torch::load( discriminator_optimizer, &quot;discriminator-optimizer-checkpoint.pt&quot;); &#125; int64_t checkpoint_counter = 1; for (int64_t epoch = 1; epoch &lt;= kNumberOfEpochs; ++epoch) &#123; int64_t batch_index = 0; for (torch::data::Example&lt;&gt;&amp; batch : *data_loader) &#123; // Train discriminator with real images. discriminator-&gt;zero_grad(); torch::Tensor real_images = batch.data.to(device); torch::Tensor real_labels = torch::empty(batch.data.size(0), device).uniform_(0.8, 1.0); torch::Tensor real_output = discriminator-&gt;forward(real_images); torch::Tensor d_loss_real = torch::binary_cross_entropy(real_output, real_labels); d_loss_real.backward(); // Train discriminator with fake images. torch::Tensor noise = torch::randn(&#123; batch.data.size(0), kNoiseSize, 1, 1 &#125;, device); torch::Tensor fake_images = generatorr-&gt;forward(noise); torch::Tensor fake_labels = torch::zeros(batch.data.size(0), device); torch::Tensor fake_output = discriminator-&gt;forward(fake_images.detach()); //std::cout &lt;&lt; fake_labels.sizes() &lt;&lt; std::endl; //std::cout &lt;&lt; fake_output.sizes() &lt;&lt; std::endl; torch::Tensor d_loss_fake = torch::binary_cross_entropy(fake_output, fake_labels); d_loss_fake.backward(); torch::Tensor d_loss = d_loss_real + d_loss_fake; discriminator_optimizer.step(); // Train generator. generatorr-&gt;zero_grad(); fake_labels.fill_(1); fake_output = discriminator-&gt;forward(fake_images); torch::Tensor g_loss = torch::binary_cross_entropy(fake_output, fake_labels); g_loss.backward(); generator_optimizer.step(); batch_index++; if (batch_index % kLogInterval == 0) &#123; std::printf( &quot;[%2ld/%2ld][%3ld/%3ld] D_loss: %.4f | G_loss: %.4f
